import React, { useEffect, useState, Fragment } from 'react';
import { Helmet } from 'react-helmet';
import { useLocation } from 'react-router-dom';

function Summary() {
  const location = useLocation();
  const [stats, setStats] = useState({
    score: 0,
    numberOfQuestions: 0,
    numberOfAnsweredQuestions: 0,
    correctAnswers: 0,
    wrongAnswers: 0,
    // hintsUsed: 0,
    // fiftyFiftyUsed: 0,
  });

  useEffect(() => {
    const { state } = location;
    setStats({
      score: (state.score / state.numberOfQuestions) * 100,
      numberOfQuestions: state.numberOfQuestions,
      numberOfAnsweredQuestions: state.numberOfAnsweredQuestions,
      correctAnswers: state.correctAnswers,
      wrongAnswers: state.wrongAnswers,
    //   hintsUsed: state.hintsUsed,
    //   fiftyFiftyUsed: state.fiftyFiftyUsed,
    });
  }, [location]);

  return (
    <Fragment>
      <Helmet>
        <title>Summary</title>
      </Helmet>
      <div className="summary-container">
        <h1>Summary</h1>
        <h4>{stats.score.toFixed(2)}%</h4>
        <div className="summary-stats">
          <p>Total number of questions: {stats.numberOfQuestions}</p>
          <p>Number of answered questions: {stats.numberOfAnsweredQuestions}</p>
          <p>Number of correct answers: {stats.correctAnswers}</p>
          <p>Number of wrong answers: {stats.wrongAnswers}</p>
          {/* <p>Number of hints used: {stats.hintsUsed}</p>
          <p>Number of 50-50 used: {stats.fiftyFiftyUsed}</p> */}
        </div>
        {/* <div className="summary-buttons">
          <Link to="/" className="btn btn-home">
            Home
          </Link>
          <Link to="/" className="btn btn-replay">
            Play Again
          </Link>
        </div> */}
      </div>
    </Fragment>
  );
}

export default Summary;


