import { Fragment, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import isEmpty from '../../utils/isEmpty';

const PlayP6English = () => {
    const [questions, setQuestions] = useState([]);
    const [currentQuestion, setCurrentQuestion] = useState({});
    const [nextQuestion, setNextQuestion] = useState({});
    const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);

    const navigate = useNavigate();

    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                const res = await axios.get('http://localhost:3000/P6EnglishQuestions')
                console.log("Fetch completed...")
                console.log("Result: " + res.data)
                const list = res.data.sort(() => 0.5 - Math.random())
                const shuffled_data = list.slice(0, 30)
                setQuestions(shuffled_data)
            }
            catch (err) {
                console.error(err)
            }
        };
        fetchQuestions();
    }, [])

    useEffect(() => {
        if (!isEmpty(questions)) {
            setCurrentQuestion(questions[currentQuestionIndex]);
            setNextQuestion(questions[currentQuestionIndex + 1]);
        }
    }, [questions, currentQuestionIndex])

    const displayQuestions = (questions = [], currentQuestion, nextQuestion) => {

        if(!isEmpty (questions)) {
            currentQuestion = questions[currentQuestionIndex];
            nextQuestion = questions[currentQuestionIndex + 1];
            setCurrentQuestion(currentQuestion);
            setNextQuestion(nextQuestion);
        }
    }

    const handleQuitButton = () => {
        if (window.confirm("Are you sure you want to quit?")) {
            navigate('/');
        }
    }

    const handleNextButton = () => {
        if (nextQuestion !== undefined) {
            setCurrentQuestionIndex(currentQuestionIndex + 1);
            displayQuestions(questions, currentQuestion, nextQuestion)
        }
    }

    return (
        <Fragment>
            <title>Domanda</title>

            <div>
                <h2>P6 English</h2>
                <h5>{currentQuestion.questionText}</h5>
            </div>

            <div className="options-container">
                        <p onClick={handleQuitButton} className="option">{currentQuestion.optionA}</p>
                        <p onClick={handleQuitButton} className="option">{currentQuestion.optionB}</p>
            </div>

            <div>
                <button onClick={handleQuitButton}>Quit</button>
                <button onClick={handleNextButton}>Next</button>
            </div>
        </Fragment>
    )
}

export default PlayP6English;
