import { Fragment, useState, useEffect } from 'react';
import { Helmet } from 'react-helmet';

const Summary = (props) => {

    const [score, setScore] = useState(0);
    const [numberOfQuestions, setNumberOfQuestions] = useState(0);
    const [numberOfAnsweredQuestions, setNumberOfAnsweredQuestions] = useState(0);
    const [correctAnswers, setCorrectAnswers] = useState(0);
    const [wrongAnswers, setWrongAnswers] = useState(0);

    useEffect(() => {
        const { state } = props.location;
        setScore((state.score / state.numberOfQuestions) * 100);
        setNumberOfQuestions(state.numberOfQuestions);
        setNumberOfAnsweredQuestions(state.numberOfAnsweredQuestions);
        setCorrectAnswers(state.correctAnswers);
        setWrongAnswers(state.wrongAnswers);
    }, [props.location]);

    return (
        <Fragment>
          <Helmet><title>Summary</title></Helmet>
          <div>
            <h1>Quiz Summary</h1>
            <div>
              <h3>{`Score: ${score.toFixed(2)}%`}</h3>
              <h3>{`Number of questions: ${numberOfQuestions}`}</h3>
              <h3>{`Number of answered questions: ${numberOfAnsweredQuestions}`}</h3>
              <h3>{`Number of correct answers: ${correctAnswers}`}</h3>
              <h3>{`Number of wrong answers: ${wrongAnswers}`}</h3>
            </div>
          </div>
        </Fragment>
      );
    }

export default Summary;
